import express from 'express';
import { 
  registrar, 
  login, 
  perfil, 
  validacionesRegistro, 
  validacionesLogin 
} from '../controllers/authController.js';
import { verificarToken } from '../middleware/auth.js';

const router = express.Router();

router.post('/registro', validacionesRegistro, registrar);
router.post('/login', validacionesLogin, login);
router.get('/perfil', verificarToken, perfil);

export default router;

import jwt from 'jsonwebtoken';
import { Usuario } from '../models/Usuario.js';
import { body, validationResult } from 'express-validator';

export const registrar = async (req, res) => {
  try {
    const errors = validationResult(req);
    if (!errors.isEmpty()) {
      return res.status(400).json({ errores: errors.array() });
    }

    const { nombre, email, password, rol, departamento, telefono } = req.body;

    const usuarioExistente = Usuario.obtenerPorEmail(email);
    if (usuarioExistente) {
      return res.status(400).json({ mensaje: 'El email ya est√° registrado' });
    }

    const nuevoUsuario = await Usuario.crear({
      nombre,
      email,
      password,
      rol: rol || 'inquilino',
      departamento,
      telefono
    });

    const { password: _, ...usuarioSinPassword } = nuevoUsuario;

    res.status(201).json({
      mensaje: 'Usuario registrado exitosamente',
      usuario: usuarioSinPassword
    });
  } catch (error) {
    console.error('Error al registrar usuario:', error);
    res.status(500).json({ mensaje: 'Error interno del servidor' });
  }
};

export const login = async (req, res) => {
  try {
    const errors = validationResult(req);
    if (!errors.isEmpty()) {
      return res.status(400).json({ errores: errors.array() });
    }

    const { email, password } = req.body;

    const usuario = Usuario.obtenerPorEmail(email);
    if (!usuario) {
      return res.status(401).json({ mensaje: 'Credenciales inv√°lidas' });
    }

    const passwordValido = await Usuario.validarPassword(password, usuario.password);
    if (!passwordValido) {
      return res.status(401).json({ mensaje: 'Credenciales inv√°lidas' });
    }

    const token = jwt.sign(
      { id: usuario.id, email: usuario.email, rol: usuario.rol },
      process.env.JWT_SECRET,
      { expiresIn: '24h' }
    );

    const { password: _, ...usuarioSinPassword } = usuario;

    res.json({
      mensaje: 'Login exitoso',
      token,
      usuario: usuarioSinPassword
    });
  } catch (error) {
    console.error('Error en login:', error);
    res.status(500).json({ mensaje: 'Error interno del servidor' });
  }
};

export const perfil = (req, res) => {
  try {
    const usuario = Usuario.obtenerPorId(req.usuario.id);
    if (!usuario) {
