import bcryptjs from 'bcryptjs';
import { getData, setData, saveData } from '../data.js';

export class Usuario {
  constructor(id, nombre, email, password, rol, departamento = null, telefono = null) {
    this.id = id;
    this.nombre = nombre;
    this.email = email;
    this.password = password;
    this.rol = rol;
    this.departamento = departamento;
    this.telefono = telefono;
  }

  static async obtenerPorEmail(email) {
    const datos = getData();
    const usuario = datos.usuarios.find(u => u.email === email);
    return usuario ? new Usuario(
      usuario.id,
      usuario.nombre,
      usuario.email,
      usuario.password,
      usuario.rol,
      usuario.departamento,
      usuario.telefono
    ) : null;
  }

  static async obtenerPorId(id) {
    const datos = getData();
    const usuario = datos.usuarios.find(u => u.id === parseInt(id));
    return usuario ? new Usuario(
      usuario.id,
      usuario.nombre,
      usuario.email,
      usuario.password,
      usuario.rol,
      usuario.departamento,
      usuario.telefono
    ) : null;
  }

  static async crear(datos) {
    const datosActuales = getData();
    const nuevoId = Math.max(...datosActuales.usuarios.map(u => u.id), 0) + 1;

    const hashedPassword = await bcryptjs.hash(datos.password, 10);

    const nuevoUsuario = {
      id: nuevoId,
      nombre: datos.nombre,
      email: datos.email,
      password: hashedPassword,
      rol: datos.rol,
      departamento: datos.departamento,
      telefono: datos.telefono
    };

    datosActuales.usuarios.push(nuevoUsuario);
    setData(datosActuales);
    await saveData();

    return new Usuario(
      nuevoUsuario.id,
      nuevoUsuario.nombre,
      nuevoUsuario.email,
      nuevoUsuario.password,
      nuevoUsuario.rol,
      nuevoUsuario.departamento,
      nuevoUsuario.telefono
    );
  }

  static async validarPassword(password, hash) {
    return await bcryptjs.compare(password, hash);
  }
}

